mapper.update:
UPDATE users
SET profile=#{profile}
where id=#{id}

UPDATE users
SET pwd=#{newPwd}
WHERE id=#{id} AND pwd=#{pwd}

UPDATE users
SET email=#{email}
WHERE id=#{id}

mapper.select:
SELECT pwd, email, profile, TO_CHAR(regdate, 'YY.MM.DD HH24:MI') AS regdate 
FROM users
WHERE id=#{id}

SELECT pwd
FROM users
WHERE id=#{id}
